<center><table border="0" cellpadding="3" cellspacing="3" bgcolor="#FFFFFF"><tr><td><?php$email = $_POST['email'];//Prepare the statementif(!($query = $con->prepare("SELECT UserID from users WHERE Email=?"))){    echo "Prepare failed: (" . $con->errno . ") " . $con->error;}    $query->bindValue(1, $email, PDO::PARAM_STR);    $query->execute();    //Check if the user entered a valid e-mail	$row_count = $query->rowCount();		if ($row_count == 0){		echo "<h3>Sorry, e-mail not found!</h3><br>";        echo "Please check it was entered correctly.<br>";        echo "<a href=\"index.php?content=forgotpass\">Try Again</a><br>";	}	else {	$row = $query->fetch();		$userID = $row['UserID'];//Reset Password//Possible password characters in an array$chars = array("a", "b" , "c" , "d", "e", "f", "g", "h", "i", "j", "k", "l", "m", "n", "o", "p", "q", "r", "s", "t", "u", "v", "w", "x", "y", "z","1", "2", "3", "4", "5", "6", "7", "8", "9", "0");//Declare password variable$password = "";//Loop gets a random char from the array & concatenates random char onto passwordfor($i=0; $i<8; $i++){	$randChar = $chars[mt_rand(0,35)];	$password .= $randChar;}    //Set new password//Prepare the statementif(!($query = $con->prepare("UPDATE users set Password = PASSWORD(?) WHERE UserID=?"))){    echo "Prepare failed: (" . $con->errno . ") " . $con->error;}    $query->bindValue(1, $password, PDO::PARAM_STR);    $query->bindValue(2, $userID, PDO::PARAM_INT);    $result = $query->execute();        if($result)	{				require_once 'getUserInfo.php';				$userName = getUserInfo($userID);    	//Prepare the e-mail	$subject = "Fantasy F1 Password";	$msg = "<html>	<p>Your Fantasy F1 password has been reset for user: ".$userName['userName'] 	."<p>Here is your new password:  <b>".$password ."</b><br>".	"<p><a href=\"http://www.bridgerest.com\fantasyf1?content=login\">Log In</a><br>".	"<p>After logging in, use the Account Settings"    ." page to change your password. </html>";		// Set Headers for HTML e-mail support & custom "from" email address	$headers = "MIME-Version: 1.0" . "\r\n";	$headers .= "Content-type:text/html;charset=UTF-8" . "\r\n";	$headers .= 'From: <noreply@fantasyf1.com>' . "\r\n";	//Send the email	$emailResult = mail($email,$subject,$msg,$headers);		//Check e-mail sent correctly	if($emailResult){				echo "<b>User Name & new Password sent to the e-mail address:  " .$email .".</b><br>";		echo "The e-mail will be from <b>noreply@fantasyf1.com</b> with subject: <b>Fantasy F1 Password</b>.<br>";		echo "If you have problems receiving the e-mail, check your spam folder.<br>";		echo "<a href=\"index.php?content=login\">Log In</a><br>";		 	}	else {				echo "<b>A problem occured while attempting to send e-mail</b>";		echo "<a href=\"index.php?content=login\">Log In</a><br>";	}}else{	echo "<b>There was a problem trying to reset your password</b>";}	}?></td></tr></table></center>